var levels = {
    'DEBUG': 0,
    'INFO': 1
}

function log(message, minLevel) {
    if (process.env.logLevel <= minLevel) {
        console.log('SUR - ' + message);
    }
}

function debug(message) {
    log(message, levels.DEBUG);
}

function info(message) {
    log(message, levels.INFO);
}


function setLevel(level) {
    process.env.logLevel = level;
}

exports.info = info;
exports.debug = debug;
exports.setLevel = setLevel;
exports.INFO = levels.INFO;
exports.DEBUG = levels.DEBUG;