var logger = require('./logger');
var loader = require('./resource-loader');
var fs = require('fs');
var path = require('path');

function route(pathname, handle, request, response) {
    logger.log('routing: ' + pathname);

    var ext = path.extname(pathname);
    switch (ext) {
        case '.css':
            loader.load(response, 'resources/css', pathname, 'css');
            break;
        case '.js':
            loader.load(response, 'resources/js', pathname, 'js');
            break;
        case '.html':
            loader.loadHtml(response, pathname);
            break;
        case '.ico':
            loader.loadIcon(response, pathname);
            break;
        default:
            if (typeof handle[pathname] === 'function') {
                return handle[pathname](request, response);
            } else {
                response.writeHead(404, {
                    "Content-Type": "text/html"
                });
                response.write('404 Not found');
                response.end();
            }
    }
}

exports.route = route;