var logger = require('./logger');

var mundi = {
    players: {}
};

function registerWaitingUser(username) {
    mundi.players[username] = {name: username, nbrGamesWon: 0, nbrGamesLost: 0};
}

function unregisterWaitingUser(username) {
    delete mundi.players[username];
}

function players() {
    return mundi.players;
}

exports.players = players;
exports.registerWaitingUser = registerWaitingUser;
exports.unregisterWaitingUser = unregisterWaitingUser;